var swiper = new Swiper('.swiper-container', {

    loop: true,
    autoplay: {
        delay: 2500,
        disableOnInteraction: false,
    },
    navigation: {
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
    },
    // pagination: {
    //     el: '.swiper-pagination',
    // },
    on: {
        slideChangeTransitionStart: function () {
            $('.disc-item').hide(0);
            $('.disc-item').removeClass('aos-init').removeClass('aos-animate');
        },
        slideChangeTransitionEnd: function () {
            $('.disc-item').show(0);
            AOS.init();
        },
    }


});

AOS.init();

$(document).ready(function () {
    $('.your-class').slick({
        slidesToShow: 4,
        slidesToScroll: 4,
        autoplay: true,
        prevArrow: false,
        nextArrow: false,
        autoplaySpeed: 1800,
        responsive: [
            {
                breakpoint: 480,
                settings: {
                    arrows: false,
                    centerMode: true,
                    centerPadding: '10px',
                    slidesToShow: 1
                }
            }
        ]
    });
});